package ipfscluster

import (
	"encoding/json"
	"testing"
)

var ccfgTestJSON = []byte(`
{
        "id": "QmUfSFm12eYCaRdypg48m8RqkXfLW7A2ZeGZb2skeHHDGA",
        "peername": "testpeer",
        "private_key": "",
        "secret": "2588b80d5cb05374fa142aed6cbb047d1f4ef8ef15e37eba68c65b9d30df67ed",
        "peers": [
           "/ip4/1.2.3.4/tcp/10000/ipfs/QmUfSFm12eYCaRdypg48m8RqkXfLW7A2ZeGZb2skeHH123"
        ],
        "bootstrap": [
           "/ip4/1.2.3.4/tcp/10000/ipfs/QmUfSFm12eYCaRdypg48m8RqkXfLW7A2ZeGZb2skeHH125"
        ],
        "leave_on_shutdown": true,
        "listen_multiaddress": "/ip4/127.0.0.1/tcp/10000",
        "state_sync_interval": "1m0s",
        "ipfs_sync_interval": "2m10s",
        "replication_factor": 5,
        "monitor_ping_interval": "2s"
}
`)

func TestLoadJSON(t *testing.T) {
	cfg := &Config{}
	err := cfg.LoadJSON(ccfgTestJSON)
	if err != nil {
		t.Fatal(err)
	}

	if cfg.Peername != "testpeer" {
		t.Error("expected peername 'testpeer'")
	}

	if len(cfg.Peers) != 1 || len(cfg.Bootstrap) != 1 {
		t.Error("expected 1 peer and 1 bootstrap")
	}

	if cfg.ReplicationFactor != 5 {
		t.Error("expected replication factor 5")
	}

	j := &configJSON{}

	json.Unmarshal(ccfgTestJSON, j)
	j.ID = "abc"
	tst, _ := json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error decoding ID")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.Peername = ""
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if cfg.Peername == "" {
		t.Error("expected default peername")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.PrivateKey = "abc"
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error parsing private key")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.ListenMultiaddress = "abc"
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error parsing listen_multiaddress")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.Secret = "abc"
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error decoding secret")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.Bootstrap = []string{"abc"}
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error decoding bootstrap address")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.Peers = []string{"abc"}
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error decoding bootstrap address")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.StateSyncInterval = ""
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if err == nil {
		t.Error("expected error state_sync_interval")
	}

	j = &configJSON{}
	json.Unmarshal(ccfgTestJSON, j)
	j.ReplicationFactor = 0
	tst, _ = json.Marshal(j)
	err = cfg.LoadJSON(tst)
	if cfg.ReplicationFactor != -1 {
		t.Error("expected default replication factor")
	}
}

func TestToJSON(t *testing.T) {
	cfg := &Config{}
	cfg.LoadJSON(ccfgTestJSON)
	newjson, err := cfg.ToJSON()
	if err != nil {
		t.Fatal(err)
	}
	cfg = &Config{}
	err = cfg.LoadJSON(newjson)
	if err != nil {
		t.Fatal(err)
	}
}

func TestDefault(t *testing.T) {
	cfg := &Config{}
	cfg.Default()
	if err := cfg.Validate(); err != nil {
		t.Fatal(err)
	}
}

func TestValidate(t *testing.T) {
	cfg := &Config{}
	cfg.Default()
	cfg.ID = ""
	if cfg.Validate() == nil {
		t.Fatal("expected error validating")
	}

	cfg.Default()
	cfg.MonitorPingInterval = 0
	if cfg.Validate() == nil {
		t.Fatal("expected error validating")
	}
}
